import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import MovieDetail from './pages/MovieDetail';
import Recommendations from './pages/Recommendations';
import GuessMovie from './pages/GuessMovie';
import MovieTrivia from './pages/MovieTrivia';
import UserPreferences from './pages/UserPreferences';
import Login from './pages/Login';
import { getCurrentUser, syncGenresFromTMDB } from './services/api';
import './App.css';

function App() {
  const [user, setUser] = useState(getCurrentUser());
  const [userPreferences, setUserPreferences] = useState({
    favoriteGenres: [],
    favoriteMovies: [],
    watchedMovies: [],
    requireAuth: false
  });

  // Sync genres from TMDB on app load
  useEffect(() => {
    const initializeApp = async () => {
      try {
        // Sync genres from TMDB API to database
        await syncGenresFromTMDB();
      } catch (error) {
        console.error('Error syncing genres from TMDB:', error);
      }
    };

    initializeApp();
  }, []);

  // Check for user on app load
  useEffect(() => {
    const currentUser = getCurrentUser();
    setUser(currentUser);
  }, []);

  const updateUserPreferences = (newPreferences) => {
    setUserPreferences(prev => ({
      ...prev,
      ...newPreferences
    }));
  };

  const handleUserChange = (newUser) => {
    setUser(newUser);
  };

  return (
    <Router>
      <div className="app">
        <Navbar user={user} handleUserChange={handleUserChange} />
        <main className="app-content">
          <Routes>
            <Route path="/" element={<Home userPreferences={userPreferences} />} />
            <Route path="/movie/:id" element={<MovieDetail userPreferences={userPreferences} updateUserPreferences={updateUserPreferences} user={user} />} />
            <Route path="/recommendations" element={<Recommendations userPreferences={userPreferences} />} />
            <Route path="/guess-movie" element={<GuessMovie />} />
            <Route path="/movie-trivia" element={<MovieTrivia />} />
            <Route path="/preferences" element={<UserPreferences userPreferences={userPreferences} updateUserPreferences={updateUserPreferences} />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

export default App;
